(1211, 'en', 'default', '36ce374000d338cd986460ce312bcae5', 'In this section you should note your site URL, as well as the name of the company, organization, or individual behind it, and some accurate contact information.', NULL, NULL, '', 'LINE', NULL, 0, '', '3ee9c2dd60584a7dde9b3eda4294c2d0', '', NULL),
(1212, 'en', 'default', '16a7d315d4eb5e71cf53bd119764f2a6', 'The amount of information you may be required to show will vary depending on your local or national business regulations. You may, for example, be required to display a physical address, a registered address, or your company registration number.', NULL, NULL, '', 'LINE', NULL, 0, '', '1309f621bd0055cd2c9d869996990f0c', '', NULL),
