(1676, 'en', 'default', '2042039084309ff51e79ad865fddfe18', 'Permalink Settings', NULL, NULL, '', 'LINE', NULL, 0, '', 'c76844e39b33480bb6fe2e4cf46768b8', '', NULL),
(1677, 'en', 'default', 'cf58d0bdb1a7290be283d6eecf7ae494', 'Permalinks are the permanent URLs to your individual pages and blog posts, as well as your category and tag archives. A permalink is the web address used to link to your content. The URL to each post should be permanent, and never change &#8212; hence the name permalink.', NULL, NULL, '', 'LINE', NULL, 0, '', '5e17346941e1c3c5429df2cd864628f8', '', NULL),
(1678, 'en', 'default', '30f9f179137df7ca8ffbfb5f2f1e3c07', 'This screen allows you to choose your permalink structure. You can choose from common settings or create custom URL structures.', NULL, NULL, '', 'LINE', NULL, 0, '', 'a24d06ba20bbc9ad1b7f8b3d334e7d15', '', NULL),
(1679, 'en', 'default', '84760e95a02e1b21fd067dcf8d357fdc', 'You must click the Save Changes button at the bottom of the screen for new settings to take effect.', NULL, NULL, '', 'LINE', NULL, 0, '', '40de51e1c0d646fdf0d7d9f4efaa3ea7', '', NULL),
(1680, 'en', 'default', '80eb21ed1fac7886258fd1da7c3268bc', 'Permalinks can contain useful information, such as the post date, title, or other elements. You can choose from any of the suggested permalink formats, or you can craft your own if you select Custom Structure.', NULL, NULL, '', 'LINE', NULL, 0, '', '341dbad1916c2decd6aead18f094b18b', '', NULL),
(1681, 'en', 'default', 'd88cf31a6a2a7495378f1004aedd2741', 'If you pick an option other than Plain, your general URL path with structure tags (terms surrounded by %s) will also appear in the custom structure field and your path can be further modified there.', NULL, NULL, '', 'LINE', NULL, 0, '', '95836dfeeb583e2f855dd2ec70778194', '', NULL),
(1682, 'en', 'default', 'd999e714d0745e52c344416fde2e7bdb', 'When you assign multiple categories or tags to a post, only one can show up in the permalink: the lowest numbered category. This applies if your custom structure includes %1$s or %2$s.', NULL, NULL, '', 'LINE', NULL, 0, '', '9dd040215a7709056c4dd347c7722bc2', '', NULL),
(1683, 'en', 'default', '313a4258db970729195e2e53fe33412e', 'Custom Structures', NULL, NULL, '', 'LINE', NULL, 0, '', '6df19e5a9afce680bb53549314ac67cd', '', NULL),
