(3054, 'en', 'default', 'dd3258e965aab5f73100334a65e08673', 'The current theme is displayed highlighted as the first theme.', NULL, NULL, '', 'LINE', NULL, 0, '', '2c4414afb28b421c247aa0e4057bfd90', '', NULL),
(3055, 'en', 'default', 'c5a4e15efc46e23f0bf0695ec14433b4', 'The search for installed themes will search for terms in their name, description, author, or tag.', NULL, NULL, '', 'LINE', NULL, 0, '', '1bc81ddaa9962129f39339b7ea4c77e4', '', NULL),
(3056, 'en', 'default', 'ff1e9b8584871613da97bf909bf0d3d4', 'If you would like to see more themes to choose from, click on the &#8220;Add New&#8221; button and you will be able to browse or search for additional themes from the <a href=\"%s\">WordPress Theme Directory</a>. Themes in the WordPress Theme Directory are designed and developed by third parties, and are compatible with the license WordPress uses. Oh, and they&#8217;re free!', NULL, NULL, '', 'LINE', NULL, 0, '', '85b7f40dae14e2dedd2d051b8da068b4', '', NULL),
