(3943, 'en', 'default', 'f84196c911ec5a65c48ccee53f33c850', 'You can choose what&#8217;s displayed on the homepage of your site. It can be posts in reverse chronological order (classic blog), or a fixed/static page. To set a static homepage, you first need to create two <a href=\"%s\">Pages</a>. One will become the homepage, and the other will be where your posts are displayed.', NULL, NULL, '', 'LINE', NULL, 0, '', '649280ffbec104f0b86be27e09164287', '', NULL),
(3944, 'en', 'default', '007231ab4175ae868fa80a67a9bd0265', 'You can also control the display of your content in RSS feeds, including the maximum number of posts to display and whether to show full text or a summary. <a href=\"%s\">Learn more about feeds</a>.', NULL, NULL, '', 'LINE', NULL, 0, '', 'a3f76c17ee95132ca44e15679558fa6a', '', NULL),
